sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord()  || newValue === '' || newValue == oldValue) {
      return;
   }
   //Type appropriate comment here, and begin script below

   var getDeptName = new GlideAjax('GetDeptName');

   getDeptName.addParam('sysparm_name', 'getDept');

   getDeptName.addParam('sysparm_user', g_form.getValue('u_dept_head'));

   // Send the request to the server
   getDeptName.getXML(populateDepartmentField);

	// When the response is back from the server
   function populateDepartmentField(response){
	var DeptFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
	alert(DeptFromScriptInclude); 
	// g_form.clearValue("u_depart");
	g_form.setValue("u_depart", DeptFromScriptInclude);

   }

}
